    def signup_check(self,Dialog):
        user = self.input_user.text()
        password = self.input_password.text()
        password2 = self.input_password2.text()
        email = self.input_email.text()
        tel = self.input_tel.text()

        con = pymysql.connect('localhost', 'root', '123456', 'mydb')
        cur = con.cursor()
        cur.execute("select UID from User where UID='" + user + "'")

        Dialog_username_error = QDialog()
        error_username = username_error.Ui_Dialog()
        error_username.setupUi(Dialog_username_error)

        Dialog_password_error = QDialog()
