        Dialog_signup_succ=QDialog()
        succ_signup=signup_succ.Ui_Dialog()
        succ_signup.setupUi(Dialog_signup_succ)

        if (cur.rowcount != 0):
            Dialog_username_error.exec()
            cur.close()
        elif (password != password2):
            Dialog_password_error.exec()
            cur.close()
        else:
            cur.execute(
                "insert into User (UID,UPassword,UEmail,UTel) "
                "values ('" + user + "','" + password + "','" + email + "','" + tel + "')")
            con.commit()
            cur.close()
            Dialog_signup_succ.exec()
            Dialog.close()